// Badge invisible elements
var i=0, sKey = false, activehCaptchaStatus = {}, activehCaptchaModes = new Array(), activehCaptchaIds = new Array();
if(getParams("hcaptcha-badge.js").key!==undefined) {
	sKey = getParams("hcaptcha-badge.js").key;
	document.querySelectorAll('form').forEach(function(node) {
		if(node.classList.contains("hcaptcha")) {
			var res = document.createElement('div');
			res.setAttribute("id", "hcaptcha"+i);
			res.setAttribute("class", "h-captcha");
			res.setAttribute("data-sitekey", getParams("hcaptcha-badge.js").key);
			res.setAttribute("data-size", "invisible");
			
			activehCaptchaModes["hcaptcha"+i] = (((node.querySelector('button[type="button"]'))!==null) ? 'manual' : 'auto');
			activehCaptchaIds.push("hcaptcha"+i);
			
			if(activehCaptchaModes["hcaptcha"+i]=="manual") {
				(node.querySelector('button[type="button"]')).addEventListener("click", function() {
					window[(node.id)+"Submit"]();
				});
				res.setAttribute("data-callback", (node.id)+"Submit");
			}
			else {
				if(node.querySelector('button[type="submit"]')!==null) {
					var subNode = (node.querySelector('button[type="submit"]'));
					subNode.setAttribute("type", "button");
					subNode.setAttribute("data-type", "hCaptcha");
				}
				(node.querySelector('button[data-type="hCaptcha"]')).addEventListener("click", function() {
					hcExecute(node);
				});
			}
			res.setAttribute("data-error-callback", "hcError");
			res.setAttribute("data-open-callback", "hcOpen");
			res.setAttribute("data-close-callback", "hcReset");
			res.setAttribute("data-chalexpired-callback", "hcReset");
			res.setAttribute("data-expired-callback", "hcReset");
			node.appendChild(res);
			i++;
		}
	});
	
	var hcApi = document.createElement('script');
	hcApi.type = 'text/javascript';
	hcApi.src = 'https://hcaptcha.com/1/api.js?onload=hcCaptchaCallback&render=explicit';
	document.head.appendChild(hcApi);
}

// Badge visible elements
if(getParams("hcaptcha-badge.js").key!==undefined) {
	var hcstyles = document.createElement("style");
	hcstyles.innerHTML = '.hcaptcha-badge{width:256px;height:60px;display:block;transition:right 0.3s ease 0s;position:fixed;bottom:14px;right:-186px;box-shadow:gray 0 0 5px;border-radius:2px;overflow:hidden;font-family:Roboto,helvetica,arial,sans-serif}.hcaptcha-badge:hover{right:-4px}.hc-a-invisible{height:60px;width:256px;display:flex}.hc-a-clr{background:#f9f9f9;color:#000}.hc-a{border-radius:3px;box-shadow:0 0 4px 1px rgba(0,0,0,0.08);-webkit-box-shadow:0 0 4px 1px rgba(0,0,0,0.08);-moz-box-shadow:0 0 4px 1px rgba(0,0,0,0.08)}.hc-a-normal-footer{display:inline-block;height:74px;vertical-align:top;width:70px}.hc-a-logo-img-large{transition:all 0.3s ease;background-size:40px;margin:5px 15px 0;height:40px;width:40px}.hc-a-logo-img{background:url("data:image/svg+xml;base64,");background-repeat:repeat;background-size:contain;background-repeat:no-repeat}.hc-a-invisible .hc-a-normal-footer .hc-a-pt{transition:opacity 0.3s ease;text-align:center;width:70px;margin-top:2px}.hc-a-compact .hc-a-pt,.hc-a-invisible .hc-a-pt,.hc-a-normal .hc-a-pt{font-family:Roboto,helvetica,arial,sans-serif;font-size:8px;font-weight:400}.hc-a-invisible-text{background:#00bdc8;color:white;display:flex;flex-basis:166px;flex-direction:column;flex-grow:1;font-family:Roboto,helvetica,arial,sans-serif;font-size:13px;font-weight:400;height:100%;justify-content:center;line-height:20px;padding:0 16px;white-space:nowrap}.hc-a-invisible-text .hc-a-pt a:link,.hc-a-invisible-text .hc-a-pt a:visited{color:white;font-size:10px}.hc-a-invisible-hover:hover .hc-a-logo-img-large,.hc-a-invisible-nohover .hc-a-logo-img-large{background-size:44px;margin:8px 13px 0;height:44px;width:44px}.hc-a-clr .hc-a-logo-text,.hc-a-clr div a:link,.hc-a-clr div a:visited{color:#555;text-decoration:none}.hc-a-invisible .hc-a-normal-footer .hc-a-pt{transition:opacity 0.3s ease;text-align:center;width:70px;margin-top:3px}.hc-a-compact .hc-a-pt,.hc-a-invisible .hc-a-pt,.hc-a-normal .hc-a-pt{font-size:8px;font-weight:400}';
	document.head.appendChild(hcstyles);
	var res = document.createElement('div');
	res.innerHTML = '<!-- Git Repo : https://github.com/jayhasgin/hcaptcha-badge; License : GPL-3.0 [https://github.com/jayhasgin/hcaptcha-badge/blob/main/LICENSE] --> <div id="hcbadgecontainer" style="opacity:0; display:none; visibility: hidden;" class="hcaptcha-badge" data-style="bottomright"><div class="hcaptcha-logo"><div id="hc-a-alert" class="hc-a-alert"></div><div class="hc-a hc-a-invisible hc-a-clr hc-a-invisible-hover"><div class="hc-a-normal-footer" aria-hidden="true"><div class="hc-a-logo-large" role="presentation"><div class="hc-a-logo-img hc-a-logo-img-large"></div></div><div id="hcvisiblea" class="hc-a-pt"><a href="https://hcaptcha.com/privacy" target="_blank">Privacy</a><span aria-hidden="true" role="presentation"> - </span><a href="https://hcaptcha.com/terms" target="_blank">Terms</a></div></div><div class="hc-a-invisible-text"><span>protected by <strong>hCaptcha</strong></span><div class="hc-a-pt"><a href="https://hcaptcha.com/privacy" target="_blank">Privacy</a><span aria-hidden="true" role="presentation"> - </span><a href="https://hcaptcha.com/terms" target="_blank">Terms</a></div></div></div></div></div>';
	document.body.appendChild(res);
	document.body.onload = function() {
		document.getElementById('hcbadgecontainer').style.visibility = 'visible';
		document.getElementById('hcbadgecontainer').style.opacity = '1';
		document.getElementById('hcbadgecontainer').style.display = 'block';
		document.getElementById("hcbadgecontainer").onmouseenter = function() {
			document.getElementById("hcvisiblea").style.display = "none";
		};
		document.getElementById("hcbadgecontainer").onmouseleave = function() {
			document.getElementById("hcvisiblea").style.display = "block";
		};
	};
}

// Badge Functions

function getParams(script) {
	var scripts = document.getElementsByTagName("script");
	for(var i=0; i<scripts.length; i++) {
		if(scripts[i].src.indexOf("/" + script) > -1) {
			var pa = scripts[i].src.split("?").pop().split("&");
			var p = {};
			for(var j=0; j<pa.length; j++) {
				var kv = pa[j].split("=");
				p[kv[0]] = kv[1];
			}
			return p;
		}
	}
	return {};
}

function hcCaptchaCallback() {
	for(var j=0; j<activehCaptchaIds.length; j++) {
		var capId = hcaptcha.render(activehCaptchaIds[j], {'sitekey' : sKey});
		document.getElementById(activehCaptchaIds[j]).setAttribute("data-hid", capId);
		activehCaptchaStatus[capId] = "rendered";
	}
}

function hcExecute(formNode) {
	if(formNode.querySelector('div[data-hid]')) {
		if(window["hcExecuteCustom"]!==undefined) { 
			window["hcExecuteCustom"]();
		}
		var capId = (formNode.querySelector('div[data-hid]')).getAttribute("data-hid");
		activehCaptchaStatus[capId] = "initialized";
		activehCaptchaStatus[capId]["check"] = setInterval(function(){
			if(activehCaptchaStatus[capId]=="reset") {
				hcaptcha.reset(capId);
				clearInterval(activehCaptchaStatus[capId]["check"]);
			}
			if(!hcaptcha.getResponse(capId) && activehCaptchaStatus[capId]=="initialized") {
				hcaptcha.execute(capId);
				activehCaptchaStatus[capId] = "executed";
			}
			if(hcaptcha.getResponse(capId)) {
				activehCaptchaStatus[capId] = "completed";
				clearInterval(activehCaptchaStatus[capId]["check"]);
				formNode.submit();
			}
		}, 2000);
	}
	else {
		console.log("hCaptcha failed to load");
	}
}

function hcReset() {
	document.querySelectorAll('form').forEach(function(node) {
		if(window["hcResetCustom"]!==undefined) { 
			window["hcResetCustom"]();
		}
		if(node.querySelector('div[data-hid]')) {
			var capId = (node.querySelector('div[data-hid]')).getAttribute("data-hid");
			activehCaptchaStatus[capId] = "reset";
			console.log("hCaptcha reset.");
		}
	});
}

function hcOpen() {
	if(window["hcOpenCustom"]!==undefined) { 
		window["hcOpenCustom"]();
	}
	console.log("hCaptcha opens.");
}

function hcError(data) {
	if(window["hcErrorCustom"]!==undefined) { 
		window["hcErrorCustom"]();
	}
	console.log("hCaptcha failed. Error:"+data);
}
